/**
 * Finds a protein within a strand of DNA represented as a string of c,g,t,a letters.
 * A protein is a part of the DNA strand marked by start and stop codons (DNA triples)
 * that is a multiple of 3 letters long.
 *
 * @author Duke Software Team 
 */
import edu.duke.*;
import java.io.*;

public class TagFinder {
    public String findGene(String dna, int where) {
        int startIndex = dna.indexOf("ATG", where);
        if (startIndex == -1) {
            return "";
        }
        int taaIndex = findStopCodon(dna,startIndex,"TAA");
        int tagIndex = findStopCodon(dna,startIndex,"TAG");
        int tgaIndex = findStopCodon(dna,startIndex,"TGA");
        int minIndex = 0;
        
        if (taaIndex == -1 ||( tgaIndex != -1 && tgaIndex < taaIndex)){
            minIndex = tgaIndex;
        }
        else {
            minIndex = taaIndex;
        }
        if (minIndex == -1|| (tagIndex != -1 && tagIndex < minIndex)){
            minIndex = tagIndex;
        }
        if(minIndex == -1){
            return "";
        }
        return dna.substring(startIndex,minIndex + 3);
    }
    
    public int findStopCodon(String dna, int startIndex, String stopCodon){
        int stopIndex = dna.indexOf(stopCodon, startIndex);
        while (stopIndex != -1){
            if ((stopIndex - startIndex) % 3 == 0){
                return stopIndex;
            }
            stopIndex = dna.indexOf(stopCodon, stopIndex+3);
        }
        return -1;   
    }
    
    public double cgRatio(String dna){
        double count = 0.0;
        count += countIndex(dna,"C");
        count += countIndex(dna,"G");
        return (float)count/dna.length();
    }
    
    public int countIndex(String dna, String target){
        int count = 0;
        int currIndex = 0;
        int lastIndex = 0;
        while (true){
            lastIndex = dna.indexOf(target,currIndex);
            if(lastIndex == -1){
                break;
            }
            currIndex = lastIndex + target.length();
            count += 1;
        }
        return count;
    }
    
    public int countCTG(String dna){
        int count = 0;
        count = countIndex(dna,"CTG");
        return count;
    }
    
    public StorageResource getAllGenes(String dna){
        StorageResource geneList = new StorageResource();
        int startIndex = 0;
        while(true){
            String currentGene = findGene(dna,startIndex);
            if(currentGene.isEmpty()){
                break;
            }
            geneList.add(currentGene);
            startIndex = dna.indexOf(currentGene,startIndex) +
                         currentGene.length();
        }
        return geneList;       
    }
    
    public void processGenes(StorageResource sr){
        int count = 0;
        for (String index : sr.data()){
            //if (index.length() > 9){
            System.out.println("GENE IS: " + index);
            count += 1;
                    
        }
        System.out.println("NUMBER OF GENES: " + count);
        int cgcount = 0;
        for (String index : sr.data()){
            System.out.println("CG RATIO: " + cgRatio(index));
            if(cgRatio(index) > 0.35){
                System.out.println("CG GENES: " + index);
                cgcount += 1;
            }
        }
        System.out.println("CG NUMBERS: " +cgcount);
        for (String index : sr.data()){
            if(index.length() > 60){
                System.out.println("long GENES: " + index);
            }
        }
    }
    
    public void testProcessGenes(){
        FileResource fr = new FileResource("brca1line.fa");
        String dna = fr.asString();
        System.out.println("-------------------------------------------------");
        dna = dna.toUpperCase();
        System.out.println("dna is: " + dna);
        StorageResource genes = getAllGenes(dna);
        for (String g: genes.data()){
            System.out.println(g);
        }
    }
    
    public void testCG(){
        System.out.println("-------------------------------------------------");
        StorageResource genes = getAllGenes("");
        processGenes(genes);
    }
    
    public void testOn(String dna) {
        System.out.println("Testing get AllGenes on " + dna);
        StorageResource genes = getAllGenes(dna);
        for (String g: genes.data()){
            System.out.println(g);
        }
    }

}
